import React from 'react';

const Card = props => {
  return (
    <div className='container'>
      {props.person.map(person => (
        <div className='card'>
          <h2>{person.name}</h2>
          <p>{person.description}</p>
        </div>
      ))}
    </div>
  );
};

export default Card;
